class AddRepoForm extends React.Component {
  constructor(props) {
      super(props);
      this.state = { value: '' };
    }

  handleInputChange = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  onClick = (e) => {
    this.setState({
      value: e.target.value,
    });
  }

  handleSearch = (e) => {
    e.preventDefault();
    if (this.state.value.length <= 0) {
      alert("Please enter a valid Repository");
    } else {
      // const redirection = `/keysearch/${this.state.value}`;
      // window.location.href = redirection;
      alert("YOU ENTERED: ", this.state.value);
    }
  };

  render() {
    const { value } = this.state;
    return (
      <div>
          <h3>Search for a GitHub Repository</h3>
          <div>
            <form onSubmit={this.handleSearch}>
              <input
                placeholder="facebook/react âŒ•"
                type="text"
                value={value}
                onChange={this.handleInputChange}
              />
              <input type="submit" onClick={this.onClick} />
            </form>
          </div>
      </div>
    );
  }
}

const App = () => {
  const [caseSensivite, setCaseSensivite] = useState(false)
  const [sortable, setSortable] = useState(true)
  const [filterable, setFilterable] = useState(true)
  const additionalCols = [
    {
      header: 'Actions',
      td: (data) => {
        return (
          <div>
            <img
              src={deleteIcon}
              alt='delete'
              width='30'
              height='20'
              onClick={() => removeWatchedRepo(data.url)}
            />
          </div>
        )
      }
    }
  ]
  return (
    <div style={{ margin: '30px' }}>
      <h2 style={{ textAlign: 'center' }}>Git Repo Tracker</h2>

      <img
        src={editIcon}
        alt='edit'
        width='30'
        height='20'
        onClick={() => addWatchedRepo("veg/hyphy")}
      />
      <AddRepoForm/>
      <ReactFlexyTable
        data={data}
        sortable={sortable}
        filterable={filterable}
        caseSensitive={caseSensivite}
        additionalCols={additionalCols}
        globalSearch
      />
    </div>
  )
}

export default App
