import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import ReactFlexyTable from 'react-flexy-table'
import 'react-flexy-table/dist/index.css'
import './index.css'
import data from './jsonData'
import {addWatchedRepo, removeWatchedRepo} from './app/Funcs.js'
import deleteIcon from './icons/delete-button-svgrepo-com.svg'
import editIcon from './icons/edit-svgrepo-com.svg'

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { username: '' };
  }
  myChangeHandler = (event) => {
    this.setState({username: event.target.value});
    console.log("HELLO?")
  }
  render() {
    return (
      // <form>
      // <h1>Hello {this.state.username}</h1>
      // <p>Enter your name:</p>
      // <input
      //   type='text'
      //   onChange={this.myChangeHandler}
      // />
      // </form>

      <form>
      <label>
        Add a Repository
        {/* <input type="text" onChange={this.myChangeHandler} /> */}
        <input type="text"  />
      </label>
      <input type="submit" value="Submit" onClick={this.myChangeHandler} />
      </form>
    );
  }
}

ReactDOM.render(<MyForm />, document.getElementById('root'));

const App = () => {
  const [caseSensivite, setCaseSensivite] = useState(false)
  const [sortable, setSortable] = useState(true)
  const [filterable, setFilterable] = useState(true)
  const additionalCols = [
    {
      header: 'Actions',
      td: (data) => {
        return (
          <div>
            <img
              src={deleteIcon}
              alt='delete'
              width='30'
              height='20'
              onClick={() => removeWatchedRepo(data.url)}
            />
          </div>
        )
      }
    }
  ]
  return (
    <div style={{ margin: '30px' }}>
      <h2 style={{ textAlign: 'center' }}>Git Repo Tracker</h2>
{/*       
      <form>
        <label>
          Add a Repository
          <input type="text" name="name"/>
        </label>
        <input type="submit" value="Submit" />
      </form> */}



      <img
        src={editIcon}
        alt='edit'
        width='30'
        height='20'
        onClick={() => addWatchedRepo("veg/hyphy")}
      />
      <MyForm/>
      <ReactFlexyTable
        data={data}
        sortable={sortable}
        filterable={filterable}
        caseSensitive={caseSensivite}
        additionalCols={additionalCols}
        globalSearch
      />
    </div>
  )
}

export default App
